import React, { useState, useCallback } from 'react';

const CARD_VALUES = {
  '2': 1, '3': 1, '4': 1, '5': 1, '6': 1,
  '7': 0, '8': 0, '9': 0,
  '10': -1, 'J': -1, 'Q': -1, 'K': -1, 'A': -1
};

const CardCounter = () => {
  const [cards, setCards] = useState([]);
  const [runningCount, setRunningCount] = useState(0);
  const [decksRemaining, setDecksRemaining] = useState(6); // Default to 6 decks
  const [showHelp, setShowHelp] = useState(false);

  const calculateTrueCount = useCallback(() => {
    if (decksRemaining <= 0) return 0;
    return Math.round((runningCount / decksRemaining) * 10) / 10; // Round to 1 decimal
  }, [runningCount, decksRemaining]);

  const handleCardInput = (card) => {
    const upperCard = card.toUpperCase();
    if (CARD_VALUES[upperCard] !== undefined) {
      const value = CARD_VALUES[upperCard];
      setCards(prev => [...prev, upperCard]);
      setRunningCount(prev => prev + value);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleCardInput(e.target.value);
      e.target.value = '';
    }
  };

  const resetCount = () => {
    setCards([]);
    setRunningCount(0);
  };

  const getBetRecommendation = () => {
    const trueCount = calculateTrueCount();
    if (trueCount >= 2) return 'Bet High';
    if (trueCount >= 1) return 'Bet Medium';
    return 'Bet Minimum';
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-gray-800 rounded-lg shadow-lg">
      <div className="mb-4">
        <h1 className="text-2xl font-bold text-white mb-2">Card Counter Trainer</h1>
        <div className="flex items-center mb-4">
          <label className="text-gray-300 mr-2">Decks Remaining:</label>
          <input
            type="number"
            min="0.5"
            step="0.5"
            value={decksRemaining}
            onChange={(e) => setDecksRemaining(parseFloat(e.target.value) || 0)}
            className="w-20 px-2 py-1 rounded bg-gray-700 text-white"
          />
        </div>
      </div>

      <div className="mb-6 p-4 bg-gray-700 rounded">
        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <div className="text-gray-400 text-sm">Running Count</div>
            <div className="text-3xl font-bold text-white">{runningCount}</div>
          </div>
          <div>
            <div className="text-gray-400 text-sm">True Count</div>
            <div className="text-3xl font-bold text-white">{calculateTrueCount()}</div>
          </div>
        </div>

        <div className="mb-2">
          <div className="text-gray-400 text-sm">Bet Suggestion</div>
          <div className="text-xl font-semibold text-green-400">{getBetRecommendation()}</div>
        </div>
      </div>

      <div className="mb-4">
        <input
          type="text"
          placeholder="Enter card (2-10, J, Q, K, A)"
          onKeyDown={handleKeyDown}
          className="w-full px-3 py-2 rounded bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
          autoFocus
        />
        <div className="text-xs text-gray-500 mt-1">Press Enter to add card</div>
      </div>

      <div className="flex justify-between items-center mb-4">
        <div className="text-sm text-gray-400">
          Cards: {cards.length} | Last: {cards[cards.length - 1] || 'None'}
        </div>
        <button
          onClick={resetCount}
          className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
        >
          Reset
        </button>
      </div>

      <div className="bg-gray-900 p-3 rounded overflow-auto max-h-40 mb-4">
        <div className="flex flex-wrap gap-1">
          {cards.map((card, index) => (
            <div key={index} className="px-2 py-1 bg-blue-600 text-white rounded text-sm">
              {card}
            </div>
          ))}
        </div>
      </div>

      <div className="text-right">
        <button 
          onClick={() => setShowHelp(!showHelp)}
          className="text-xs text-blue-400 hover:text-blue-300"
        >
          {showHelp ? 'Hide Help' : 'Show Help'}
        </button>
      </div>

      {showHelp && (
        <div className="mt-4 p-3 bg-gray-700 rounded text-sm">
          <h3 className="font-semibold text-white mb-2">Hi-Lo Card Counting System</h3>
          <ul className="space-y-1 text-gray-300">
            <li>+1: 2, 3, 4, 5, 6</li>
            <li>0: 7, 8, 9</li>
            <li>-1: 10, J, Q, K, A</li>
          </ul>
          <div className="mt-2 text-xs text-gray-400">
            True Count = Running Count ÷ Decks Remaining
          </div>
        </div>
      )}
    </div>
  );
};

export default CardCounter;
