import React, { useState, useCallback, useEffect, useMemo } from 'react';
import { COUNTING_SYSTEMS, calculateTrueCount, getBetRecommendation } from '../utils/countingSystems';

const CardCounter = () => {
  // Game state
  const [decksRemaining, setDecksRemaining] = useState(3); // Default to 3 decks
  const [runningCount, setRunningCount] = useState(0);
  const [showHelp, setShowHelp] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedSystem, setSelectedSystem] = useState('HILO');
  const [showSystemInfo, setShowSystemInfo] = useState(false);
  const [error, setError] = useState('');

  // Track hands
  const [playerHands, setPlayerHands] = useState([[]]); // Array of hands for splits
  const [dealerHand, setDealerHand] = useState([]);
  const [currentHandIndex, setCurrentHandIndex] = useState(0);

  // Current cards in play (for counting)
  const [cards, setCards] = useState([]);

  // Get current counting system
  const currentSystem = useMemo(() => COUNTING_SYSTEMS[selectedSystem], [selectedSystem]);

  // Calculate deck information
  const totalCards = useMemo(() => Math.floor(decksRemaining * 52), [decksRemaining]);
  const remainingCards = useMemo(() => Math.max(0, totalCards - cards.length), [totalCards, cards.length]);
  const remainingDecks = useMemo(() => Math.max(0.1, Math.round((remainingCards / 52) * 100) / 100), [remainingCards]);
  const usedDecks = useMemo(() => Math.max(0, Math.round((decksRemaining - remainingDecks) * 100) / 100), [decksRemaining, remainingDecks]);

  // Alias remainingDecks as decksLeft for backward compatibility
  const decksLeft = remainingDecks;

  // State for manual card inputs
  const [playerCardsInput, setPlayerCardsInput] = useState('');
  const [dealerCardsInput, setDealerCardsInput] = useState('');

  // Parse card string into array of card objects
  const parseCards = (input) => {
    if (!input.trim()) return [];
    // Split by comma or space, then trim each card
    return input.split(/[,\s]+/)
      .map(card => card.trim().toUpperCase())
      .filter(card => card.length > 0)
      .map(card => {
        // Extract rank and suit (e.g., 'AH' -> {rank: 'A', suit: 'H'})
        const rank = card.replace(/[^A-Z0-9]/g, '');
        const suit = card.replace(/[^HDCS]/i, '').toUpperCase();
        return { rank, suit };
      });
  };

  // Get current player and dealer cards
  const playerCards = useMemo(() => parseCards(playerCardsInput), [playerCardsInput]);
  const dealerCards = useMemo(() => parseCards(dealerCardsInput), [dealerCardsInput]);

  // Calculate hand value
  const calculateHandValue = useCallback((hand) => {
    let value = 0;
    let aces = 0;

    hand.forEach(card => {
      const rank = card.rank;
      if (rank === 'A') {
        aces += 1;
        value += 11;
      } else if (['K', 'Q', 'J', '10'].includes(rank)) {
        value += 10;
      } else if (rank && !isNaN(rank)) {
        value += parseInt(rank);
      }
    });

    // Handle aces
    while (value > 21 && aces > 0) {
      value -= 10;
      aces -= 1;
    }

    return value;
  }, []);

  // Calculate hand values
  const playerHandValue = useMemo(() => calculateHandValue(playerCards), [playerCards, calculateHandValue]);
  const dealerHandValue = useMemo(() => calculateHandValue(dealerCards), [dealerCards, calculateHandValue]);

  // Update running count when cards change
  useEffect(() => {
    // Combine all cards and update running count
    const allCards = [...playerCards, ...dealerCards];
    const newCount = allCards.reduce((count, card) => {
      return count + (currentSystem.cardValues[card.rank] || 0);
    }, 0);

    setRunningCount(newCount);
    setCards(allCards);
  }, [playerCards, dealerCards, currentSystem]);

  // Calculate true count based on remaining decks
  const trueCount = useMemo(() => {
    if (remainingDecks <= 0) return 0;
    // For unbalanced systems, return running count directly
    if (!COUNTING_SYSTEMS[selectedSystem].balanced) {
      return runningCount;
    }
    // For balanced systems, calculate true count
    const tc = runningCount / remainingDecks;
    return Math.round(tc * 10) / 10; // Round to 1 decimal
  }, [runningCount, remainingDecks, selectedSystem]);

  // Get bet recommendation based on true count and system
  const betRec = useMemo(() => 
    getBetRecommendation(trueCount, selectedSystem)
  , [trueCount, selectedSystem]);

  // Simulate loading
  useEffect(() => {
    const timer = setTimeout(() => setIsLoading(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  const handleCardInput = (card) => {
    const upperCard = card.toUpperCase().trim();

    // Clear previous errors
    setError('');

    // Check if input is empty
    if (!upperCard) {
      setError('Please enter a card value');
      return;
    }

    // Check if card is valid in current counting system
    if (currentSystem.cardValues[upperCard] === undefined) {
      setError(`Invalid card: ${upperCard}. Valid cards are: ${Object.keys(currentSystem.cardValues).join(', ')}`);
      return;
    }

    // If we get here, the card is valid
    const value = currentSystem.cardValues[upperCard];
    setCards(prev => [...prev, { card: upperCard, value }]);
    setRunningCount(prev => prev + value);

    // Clear any previous error
    setError('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.target.value) {
      handleCardInput(e.target.value);
      e.target.value = '';
    }
  };

  const resetCount = () => {
    setCards([]);
    setRunningCount(0);
  };

  const removeLastCard = () => {
    if (cards.length > 0) {
      const lastCard = cards[cards.length - 1];
      setCards(prev => prev.slice(0, -1));
      setRunningCount(prev => prev - lastCard.value);
    }
  };

  const getCardColorClass = (value) => {
    if (value > 0) return 'bg-green-900/50 text-green-300';
    if (value < 0) return 'bg-red-900/50 text-red-300';
    return 'bg-gray-700/50';
  };

  // usedDecks and decksLeft are already defined above with useMemo

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <h1 className="text-2xl font-bold text-white">Card Counter Pro</h1>
          <p className="text-gray-400 mt-2">Loading trainer...</p>
        </div>
      </div>
    );
  }

  // Render a card with proper styling
  const renderCard = (card, index) => (
    <div 
      key={index}
      className={`inline-block w-8 h-12 md:w-10 md:h-14 mx-0.5 rounded-md flex items-center justify-center text-xs font-bold ${
        currentSystem.cardValues[card.rank] > 0 ? 'bg-green-900/50 text-green-300' :
        currentSystem.cardValues[card.rank] < 0 ? 'bg-red-900/50 text-red-300' :
        'bg-white/10 text-white'
      }`}
    >
      {card.rank}{card.suit}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4 md:p-6">
      <div className="max-w-2xl mx-auto bg-gray-800 rounded-xl shadow-2xl overflow-hidden"
           style={{ minHeight: '90vh', display: 'flex', flexDirection: 'column' }}>
        {/* Header */}
        <div className="bg-gray-900 p-6 border-b border-gray-700">
          <h1 className="text-2xl font-bold text-center">Card Counter Trainer</h1>
          <p className="text-center text-gray-400 text-sm mt-1">{currentSystem.name} System</p>

          <div className="mt-4">
            <div className="flex items-center justify-center gap-2">
              <label htmlFor="counting-system" className="text-sm text-gray-300">
                Counting System:
              </label>
              <div className="relative">
                <select
                  id="counting-system"
                  value={selectedSystem}
                  onChange={(e) => {
                    setSelectedSystem(e.target.value);
                    setCards([]);
                    setRunningCount(0);
                  }}
                  className="bg-gray-800 border border-gray-700 text-white text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 p-2 pr-8 appearance-none"
                >
                  {Object.values(COUNTING_SYSTEMS).map((system) => (
                    <option key={system.id} value={system.id}>
                      {system.name}
                    </option>
                  ))}
                </select>
                <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-400">
                  <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                    <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/>
                  </svg>
                </div>
              </div>
              <button
                onClick={() => setShowSystemInfo(!showSystemInfo)}
                className="text-blue-400 hover:text-blue-300 text-sm"
                aria-label="System info"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h.01a1 1 0 100-2H10V9z" clipRule="evenodd" />
                </svg>
              </button>
            </div>

            {showSystemInfo && (
              <div className="mt-3 p-3 bg-gray-800 rounded-lg text-sm">
                <h4 className="font-semibold text-white mb-1">{currentSystem.name} System</h4>
                <p className="text-gray-300 mb-2">{currentSystem.description}</p>
                <div className="grid grid-cols-3 gap-2 text-xs">
                  <div className="p-2 bg-green-900/30 rounded">
                    <div className="font-bold">+{Object.entries(currentSystem.cardValues).filter(([_, v]) => v > 0)[0]?.[1] || 0}</div>
                    <div>{Object.entries(currentSystem.cardValues)
                      .filter(([_, v]) => v > 0)
                      .map(([k]) => k)
                      .join(', ')}
                    </div>
                  </div>
                  <div className="p-2 bg-gray-700/30 rounded">
                    <div className="font-bold">0</div>
                    <div>{Object.entries(currentSystem.cardValues)
                      .filter(([_, v]) => v === 0)
                      .map(([k]) => k)
                      .join(', ')}
                    </div>
                  </div>
                  <div className="p-2 bg-red-900/30 rounded">
                    <div className="font-bold">{Object.entries(currentSystem.cardValues).filter(([_, v]) => v < 0)[0]?.[1] || 0}</div>
                    <div>{Object.entries(currentSystem.cardValues)
                      .filter(([_, v]) => v < 0)
                      .map(([k]) => k)
                      .join(', ')}
                    </div>
                  </div>
                </div>
                {!currentSystem.balanced && (
                  <div className="mt-2 text-xs text-yellow-300">
                    Note: This is an unbalanced system. The running count is used directly for betting.
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Main Content */}
        <div className="p-6 flex-grow">
          {/* Player and Dealer Cards */}
          <div className="space-y-6 mb-6">
            {/* Dealer's Cards */}
            <div className="bg-gray-900/30 p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-300 mb-2">Dealer's Cards</h3>
              <div className="mb-3 min-h-16">
                {dealerCards.length > 0 ? (
                  <div className="flex flex-wrap gap-1">
                    {dealerCards.map((card, index) => (
                      <div key={`dealer-${index}`}>
                        {renderCard(card, index)}
                      </div>
                    ))}
                    <div className="self-center ml-2 text-sm">
                      ({dealerHandValue} {dealerCards.some(c => c.rank === 'A' && dealerHandValue <= 11) ? '/soft' : ''})
                    </div>
                  </div>
                ) : (
                  <div className="text-gray-500 text-sm">No cards yet</div>
                )}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={dealerCardsInput}
                  onChange={(e) => setDealerCardsInput(e.target.value)}
                  placeholder="Enter dealer cards (e.g., AH, 10D, 3C)"
                  className="flex-1 bg-gray-700 border border-gray-600 text-white text-sm rounded-l-lg p-2.5 focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
                />
              </div>
            </div>

            {/* Player's Cards */}
            <div className="bg-gray-900/30 p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-300 mb-2">Your Cards</h3>
              <div className="mb-3 min-h-16">
                {playerCards.length > 0 ? (
                  <div className="flex flex-wrap gap-1">
                    {playerCards.map((card, index) => (
                      <div key={`player-${index}`}>
                        {renderCard(card, index)}
                      </div>
                    ))}
                    <div className="self-center ml-2 text-sm">
                      ({playerHandValue} {playerCards.some(c => c.rank === 'A' && playerHandValue <= 11) ? '/soft' : ''})
                    </div>
                  </div>
                ) : (
                  <div className="text-gray-500 text-sm">No cards yet</div>
                )}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={playerCardsInput}
                  onChange={(e) => setPlayerCardsInput(e.target.value)}
                  placeholder="Enter your cards (e.g., AH, 10D, 3C)"
                  className="flex-1 bg-gray-700 border border-gray-600 text-white text-sm rounded-l-lg p-2.5 focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
                />
              </div>
            </div>
          </div>

          {/* Count Display */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            {/* Running Count */}
            <div className="bg-gray-900/50 p-4 rounded-lg">
              <div className="flex items-center justify-between mb-2">
                <h3 className="text-sm font-medium text-gray-300">Running Count</h3>
                <span className="text-xs text-gray-400">{currentSystem.name}</span>
              </div>
              <div className="text-3xl font-bold">{runningCount >= 0 ? `+${runningCount}` : runningCount}</div>
              <div className="text-xs text-gray-400 mt-1">Current count based on cards played</div>
            </div>

            {/* True Count */}
            <div className="bg-gray-900/50 p-4 rounded-lg">
              <div className="flex items-center justify-between mb-2">
                <h3 className="text-sm font-medium text-gray-300">True Count</h3>
                <span className="text-xs text-gray-400">Decks left: {decksLeft.toFixed(1)}</span>
              </div>
              <div className="text-3xl font-bold">
                {trueCount >= 0 ? `+${trueCount.toFixed(1)}` : trueCount.toFixed(1)}
              </div>
              <div className="text-xs text-gray-400 mt-1">Running count / decks remaining</div>
            </div>

            {/* Bet Recommendation */}
            <div className="bg-gray-900/50 p-4 rounded-lg">
              <div className="flex items-center justify-between mb-2">
                <h3 className="text-sm font-medium text-gray-300">Bet</h3>
                <span className="text-xs text-gray-400">Recommendation</span>
              </div>
              <div className="text-3xl font-bold">{betRec.unit}</div>
              <div className="text-xs text-gray-400 mt-1">{betRec.text}</div>
            </div>
          </div>

          {/* Deck Information */}
          <div className="mb-6 bg-gray-900/30 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-gray-300 mb-3">Deck Information</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-xs text-gray-400 mb-1">Number of Decks</label>
                <div className="flex items-center">
                  <button
                    onClick={() => setDecksRemaining(prev => Math.max(1, Math.floor(prev) - 1))}
                    className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded-l-md transition-colors"
                  >
                    -
                  </button>
                  <input
                    type="number"
                    min="1"
                    max="10"
                    value={decksRemaining}
                    onChange={(e) => setDecksRemaining(Math.max(1, Math.min(10, Number(e.target.value) || 1)))}
                    className="w-16 text-center bg-gray-800 border-t border-b border-gray-700 py-1 text-white"
                  />
                  <button
                    onClick={() => setDecksRemaining(prev => Math.min(10, Math.ceil(prev) + 1))}
                    className="px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded-r-md transition-colors"
                  >
                    +
                  </button>
                </div>
              </div>

              <div>
                <div className="text-xs text-gray-400 mb-1">Cards in Play</div>
                <div className="text-sm">
                  {cards.length} / {totalCards} cards ({usedDecks.toFixed(1)} decks used, {remainingDecks.toFixed(1)} decks left)
                </div>
              </div>
            </div>
          </div>

          {/* Error message */}
          {error && (
            <p id="card-error" className="mt-2 text-sm text-red-400">
              <svg className="w-4 h-4 inline mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              {error}
            </p>
          )}

          {/* Quick Buttons */}
          <div className="grid grid-cols-5 gap-2 mt-3">
            {Object.entries(currentSystem.cardValues).map(([card]) => (
              <button
                key={card}
                onClick={() => handleCardInput(card)}
                className={`px-2 py-2 text-sm font-medium rounded-md transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900 ${
                  getCardColorClass(currentSystem.cardValues[card])
                }`}
              >
                {card}
              </button>
            ))}
          </div>
        </div>

        {/* Card History */}
        <div className="mb-6">
          <div className="flex justify-between items-center mb-2">
            <h3 className="text-sm font-medium text-gray-300">Card History</h3>
            <div className="flex gap-2">
              <button
                onClick={removeLastCard}
                disabled={cards.length === 0}
                className="text-xs px-3 py-1.5 bg-gray-700 hover:bg-gray-600 rounded-md transition-colors disabled:opacity-50 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900 focus:ring-blue-500"
              >
                Undo Last
              </button>
              <button
                onClick={resetCount}
                className="text-xs px-3 py-1.5 bg-red-900/50 hover:bg-red-800/50 rounded-md transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900 focus:ring-red-500"
              >
                Reset All
              </button>
            </div>
          </div>
          <div className="bg-gray-800/50 rounded-lg p-3 min-h-20 max-h-48 overflow-y-auto">
            {cards.length > 0 ? (
              <div className="flex flex-wrap gap-2">
                {cards.map(({card, value}, index) => (
                  <div
                    key={index}
                    className={`px-2.5 py-1 rounded-md text-sm font-mono ${getCardColorClass(value)}`}
                  >
                    {card}
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-sm text-center py-4">
                No cards added yet. Start by entering cards above.
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Help Panel */}
      {showHelp && (
        <div className="bg-gray-800 p-6 border-t border-gray-700">
          <h3 className="text-lg font-bold mb-3">How to Use This Trainer</h3>
          <ul className="space-y-2 text-sm text-gray-300">
            <li>• Select your preferred counting system from the dropdown menu</li>
            <li>• Enter cards as they are dealt using the input field or quick buttons</li>
            <li>• The running count updates automatically with each card</li>
            <li>• The true count is calculated based on the selected system and decks remaining</li>
            <li>• Use the slider to adjust the number of decks in play</li>
            <li>• The betting recommendation suggests actions based on the true count</li>
            <li>• Click "Undo Last" to remove the most recent card if you make a mistake</li>
          </ul>
          <div className="mt-4">
            <h4 className="font-semibold text-white mb-2">About Counting Systems:</h4>
            <ul className="space-y-2 text-sm">
              <li>• <span className="font-medium">Hi-Lo:</span> Balanced system, requires true count conversion</li>
              <li>• <span className="font-medium">KO:</span> Unbalanced system, uses running count directly</li>
              <li>• <span className="font-medium">Omega II:</span> Advanced balanced system with higher playing efficiency</li>
            </ul>
          </div>
          <button
            onClick={() => setShowHelp(false)}
            className="mt-6 w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-blue-500"
          >
            Got it!
          </button>
        </div>
      )}

      {/* Footer */}
      <div className="bg-gray-900 p-3 border-t border-gray-800 flex justify-between items-center">
        <button
          onClick={() => setShowHelp(!showHelp)}
          className="text-sm text-blue-400 hover:text-blue-300 flex items-center gap-1.5 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-gray-900 rounded px-2 py-1"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-8-3a1 1 0 00-.867.5 1 1 0 11-1.731-1A3 3 0 0113 8a3.001 3.001 0 01-2 2.83V11a1 1 0 11-2 0v-1a1 3 0 011.5-2.6 1 1 0 10-1-1.73z" clipRule="evenodd" />
          </svg>
          {showHelp ? 'Hide Help' : 'Show Help'}
        </button>
        <div className="text-xs text-gray-500">
          {cards.length} cards • {usedDecks} decks used • {remainingDecks} decks left
        </div>
      </div>
    </div>
  );
};

export default CardCounter;
